package Excersie_Pgm;

public class EvenOrOddProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		int b=20;
		int mod;
		mod=a%b;
		System.out.println(mod);
		if(a%b==0)
		{
			System.out.println("even");
		}
		else
		{
		System.out.println("odd");
		}

	}

}
