package mypackage;

public class Assignment8_EvenOdd {

	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=100;i++)
		{
			
			if(i%2==0)
			{
				System.out.println("even num= "+i);
			}
			else
			{
				System.out.println("odd num= "+i);
			}
		}

	}

}
